  state = {
    title: this.props.title,
    userDetail: this.props.userDetail
  }

  // componentDidMount() {
  //   this.setState({
  //     userDetail: this.props.userDetail
  //   })
  // }

  capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  render() {
    //todo allow editable values
    console.log('state', this.state.userDetail)
    console.log('props', this.props.userDetail)
    return (
        <div className="col-lg-3 col-sm-12">
          <table
              className="table table-bordered table-striped table-responsive-stack"
              id="tableOne">
            <thead className="thead-dark">
            <tr>
              <th>{this.capitalizeFirstLetter(this.state.title)}</th>
            </tr>
            </thead>
            <tbody>
            <tr>
              <td><span>{this.props.userDetail}</span>
                <i className="fas fa-pencil-alt float-right"/></td>
            </tr>
            </tbody>
          </table>
        </div>
    )
  }
}
