// const server = 'https://webdev-backend-alpha1.herokuapp.com';
const server = 'http://localhost:3001';

export const searchFlights = async (origin, destination, adults, date) => {
  const response = await fetch(
      `${server}/api/flights/${origin}/${destination}/${adults}/${date}`);
  return await response.json()
};

export const registerUser = async (user) => {
  const response = await fetch(
      `${server}/api/user`, {
        method: 'POST',
        body: JSON.stringify(user),
        headers: {
          "content-type": 'application/json'
        },
        credentials: "include"
      });
  return await response.json()
};

export const loginUser = async (user) => {
  const response = await fetch(
      `${server}/api/login`, {
        method: 'POST',
        body: JSON.stringify(user),
        headers: {
          "content-type": 'application/json'
        },
        credentials: "include"
      });
  return await response.json()
};

export const fetchProfile = async () => {
  const response = await fetch(
      `${server}/api/profile`, {
        method: 'POST',
        credentials: "include"
      });
  return await response.json()
};

export const logoutUser = async () => {
  const response = await fetch(
      `${server}/api/logout`, {
        method: 'POST',
        credentials: "include"
      });
  return await response.json()
};

export const bookTicket = async (user) => {
  const response = await fetch(`${server}/api/user/${user._id}`,{
    method:'PUT',
    body: JSON.stringify(user),
    headers: {
      'content-type': 'application/json'
    },
    credentials: "include"
  });
  return await response.json()
};
