import React from "react";
import HeaderComponent from "../Headers/HeaderComponent";
import {fetchProfile, searchHotels} from "../../services/services";
import Loader from "../../images/loader.gif";
import HotelContentComponent from "./HotelContentComponent";

export default class HotelComponent extends React.Component {

  state = {
    user: {
      email: '',
      first: '',
      last: '',
      type: '',
      ticketsBooked: []
    },
    login: false,
    loading: false,
    location: '',
    hotels: []
  };

  componentDidMount() {
    fetchProfile().then(user => {
      if (user) {
        this.setState({
          user: user,
          login: true
        })
      } else {
        window.confirm('Please Login to continue');
        this.props.history.push('/')
      }
    });
    if (this.props.location) {
      {console.log(this.props.l)}
      this.setState({
        location: this.props.location,
        loading: true
      }, () => {
        searchHotels(this.props.location).then(r => {
          this.setState({
            hotels: r,
            loading: false
          })
        })
      })
    }
  }

  loadSpinner = () => {
    this.setState({
      loading: true
    })
  };

  search = () => {
    if (this.state.location) {
      this.props.history.push(`/search/hotels/${this.state.location}`)
    }
  };

  render() {
    return (
        <>
          <HeaderComponent
              history={this.props.history}
              login={this.state.login}
              user={this.state.user}
          />
          <div className='container container-background'>
            <div className="row pt-3">
              <div className='col'>
                <h4>Search for Hotels</h4>
              </div>
            </div>
            <div className='row pt-3 pb-3'>
              <div className='col-lg-10'>
                <input className='form-control'
                       placeholder='Enter location'
                       value={this.state.location}
                       onChange={(e) => this.setState(
                           {location: e.target.value})}
                />
              </div>
              <div className='col-lg-2'>
                <button className='btn btn-success btn-block'
                        onClick={this.search}
                >Search
                </button>
              </div>
            </div>
            <div className='row mt-3 pb-3'>
              {
                this.state.loading &&
                <img src={Loader} alt="Loading..."
                     className="custom-align"/>
              }
              {
                (!this.state.loading && this.state.hotels.length !== 0) &&
                <HotelContentComponent
                    hotels={this.state.hotels}
                    login={this.state.login}
                    history={this.props.history}
                />
              }
            </div>
          </div>
        </>
    )
  }
}
