import React from "react";
import TicketsModal from "./TicketsModal";

export default class AdminContentComponent extends React.Component {
  state = {
    user: this.props.user,
    editing: false
  };

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (this.props.user._id !== prevProps.user._id) {
      this.setState({
        user: this.props.user
      })
    }
  }

  save = () => {
    this.props.updateUserService(this.state.user);
    this.setState({
      editing: false
    })
  };

  render() {

    let pass = this.state.user.password.slice(0, 45);
    return (
        <tr>
          <th scope="row">
            {!this.state.editing &&
            <span>{this.state.user.first}</span>
            }

            {this.state.editing &&
            <span>
              <input className="form-control"
                     value={this.state.user.first}
                     onChange={(e) => this.setState({
                       user: {
                         ...this.state.user,
                         first: e.target.value
                       }
                     })}
              />
            </span>}
          </th>

          <td>
            {!this.state.editing &&
            <span>{this.state.user.last}</span>
            }
            {this.state.editing &&
            <span>
              <input className="form-control"
                     value={this.state.user.last}
                     onChange={(e) => this.setState({
                       user: {
                         ...this.state.user,
                         last: e.target.value
                       }
                     })}
              />
            </span>}
          </td>

          <td>
            {!this.state.editing &&
            <i className="fas fa-pencil-alt"
               onClick={() => this.setState({
                 editing: true
               })}
            />}
            {this.state.editing &&
            <i className="fas fa-check color-green"
               onClick={this.save}/>
            }
          </td>
          <td>{this.state.user.email}</td>
          <td>{pass.concat("...")}</td>
          <td>
            <TicketsModal
                id={this.state.user.first}
                tickets={this.props.user.ticketsBooked}
            />
          </td>
          <td className="text-center">
            <i className="fas fa-trash-alt"
               onClick={() => this.props.delete(this.state.user._id)}/></td>
        </tr>
    )
  }
}
