import React from "react";
import HeaderComponent from "../Headers/HeaderComponent";
import {deleteUser, fetchProfile} from "../../services/services";
import './profile-styles.css';
import ProfileContentComponent from "./ProfileContentComponent";
import {TicketComponent} from "./TicketComponent";

export default class ProfileComponent extends React.Component {

  constructor(props){
    super(props);
    let {_id, ticketsBooked, created, ...userDetails} = this.state.user;
  }

  state = {
    user: {
      email: '',
      first: '',
      last: '',
      type: '',
      ticketsBooked: []
    },
    login: false
  };

  componentDidMount() {
    fetchProfile().then(user => {
      if (user) {
        this.setState({
          user: user,
          login: true
        })
      } else {
        this.props.history.push('/')
      }
    });
    //Todo
    /*  // for responsive tables
        // inspired by http://jsfiddle.net/arunpjohny/564Lxosz/1/
        window.$('.table-responsive-stack').find("th").each(function (i) {
          window.$('.table-responsive-stack td:nth-child(' + (i + 1) + ')').prepend(
              '<span class="table-responsive-stack-thead">' + window.$(this).text()
              + ':</span> ');
          window.$('.table-responsive-stack-thead').hide();
        });
        window.$('.table-responsive-stack').each(function () {
          let thCount = window.$(this).find("th").length;
          let rowGrow = 100 / thCount + '%';
          //console.log(rowGrow);
          window.$(this).find("th, td").css('flex-basis', rowGrow);
        });
        this.flexTable();
        window.addEventListener("resize", this.flexTable);*/
  }

  flexTable = () => {
    if ((window.innerWidth < 768)) {
      window.$(".table-responsive-stack").each(function (i) {
        window.$(this).find(".table-responsive-stack-thead").show();
        window.$(this).find('thead').hide();
      });

      // window is less than 768px
    } else {
      window.$(".table-responsive-stack").each(function (i) {
        window.$(this).find(".table-responsive-stack-thead").hide();
        window.$(this).find('thead').show();
      });
    }
  };

  deleteAccount = () => {
    if(window.confirm('Are you sure?')){
      // console.log(this.state.user._id)
      deleteUser(this.state.user._id).then(r=>{
        if(r){
          alert(`Your account with ${r.email} has been deleted!`)
          this.props.history.push('/')
        }
      })
    }
  };


  render() {


    console.log('userdetails' , this.userDetails)

    return (
        <>
          <HeaderComponent
              user={this.state.user}
              login={this.state.login}
              history={this.props.history}
          />
          <div className='container container-background'>
            <div className='row pt-3'>
              <div className='col-10' style={{"display": "inline"}}>
                <h2>Hello {this.state.user.first}!</h2>
              </div>
              <div className='col-2'>
                <button className='btn btn-link color-red float-right'
                onClick={this.deleteAccount}
                >Delete
                  Account <i className="fas fa-times"/></button>
              </div>
            </div>
            <hr/>
            <h4>My Details:</h4>
           <div className='row'>
             {console.log('usd' ,this.userDetails)}
              {Object.keys(this.userDetails).map(key =>
                  <ProfileContentComponent
                      key={key}
                      title={key}
                      userDetail={userDetails[key]}
                  />
              )}
            </div>
            <div className='row pt-3'>
              <div className='col-12'>
                <h4>Booking History:</h4>
              </div>
            </div>
            <hr/>
            <div className='row pl-3 pr-3'>
              {this.state.user.ticketsBooked.map(ticket =>
                  <TicketComponent
                      ticket={ticket}/>
              )}
            </div>
          </div>
        </>
    )
  }
}
