import React, { Component } from 'react';
import {fetchProfile} from "../../services/services";
import HeaderComponent from "../Headers/HeaderComponent";
import {fetchCurrencyList, convertCurrency} from "../../services/services";

export default class CurrencyComponent extends Component {

constructor(props) {
        super(props);
    }

    state = {
        user: {
          email: '',
          first: '',
          last: '',
          type: '',
          ticketsBooked: []
        },
        login: false,
        currenyList: [],
        from: '',
        to: '',
        amount: '',
        convertedCurrency: ''
      };

      componentDidMount() {
        fetchProfile().then(user => {
          if (user) {
            this.setState({
              user: user,
              login: true
            })
          }
        })

        fetchCurrencyList().then(list => {
            this.setState({currenyList: list})

        })


      }

      convertButton =() => {
          convertCurrency(this.state.from, this.state.to, this.state.amount).then
      }

    render() {

        console.log(this.state.currenyList)
        return (
            <div>
          <HeaderComponent
              user={this.state.user}
              login={this.state.login}
              history={this.props.history}
          />

          <input type="number" onChange = {(e) => this.setState({amount: e.target.value})} />

          <select>
            {
                Object.entries(this.state.currenyList).map(([key, value]) =>
                 <option value={key} onChange = {(e) => this.setState({from: e.target.value})} >{key}</option>)
            }

          </select>

          <select>
            {
                Object.entries(this.state.currenyList).map(([key, value]) =>
                 <option value={key} onChange = {(e) => this.setState({to: e.target.value})} >{key}</option>)
            }

          </select>

          <input readOnly type="text" value={this.state.convertedCurrency}/>

          <button className="btn btn-success" oncl>Convert</button>


            </div>
        );
    }
}
