import React from "react";

export default class ProfileContentComponent extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      title: props.title,
      userDetail: props.userDetail,
      editing: false
    };
  }

  componentDidUpdate(prevProps, prevState, snapShot) {
    if (prevProps.userDetail !== this.props.userDetail) {
      this.setState({
        title: this.props.title,
        userDetail: this.props.userDetail
      })
    }
  }

  capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  save = () => {
    let modifiedUser = {};
    modifiedUser[this.state.title] = this.state.userDetail;
    this.props.updateUser(modifiedUser);
    this.setState({
      editing: false
    })
  };

  render() {

    return (
        <div className="col-lg-3 col-sm-12">
          <table
              className="table table-bordered table-striped table-sm"
              id="tableOne">
            <thead className="thead-dark">
            <tr>
              <th>
                <h6>{this.capitalizeFirstLetter(this.state.title)}</h6>
              </th>
            </tr>
            </thead>
            <tbody>
            <tr>
              <td>
                {(!this.state.editing) &&
                <span>
                  {this.state.userDetail}

                  {(this.state.userDetail !== "USER" && this.state.userDetail
                      !== "ADMIN" && this.state.userDetail !== "AGENT" && this.state.title !== "email") &&
                  <button className="btn btn-link float-right"
                          onClick={() => this.setState({editing: true})}
                  >Change</button>
                  }
                </span>}

                {this.state.editing &&
                <span style={{"display": "inline-flex"}}>
                      <input className="form-control"
                             value={this.state.userDetail}
                             onChange={(e) => this.setState(
                                 {userDetail: e.target.value})}
                      />
                  {this.state.userDetail !== 'USER' &&
                  <i className="fa-2x fas fa-check float-right"
                     style={{"color": "green"}}
                     onClick={this.save}/>}
                    </span>
                }
              </td>
            </tr>
            </tbody>
          </table>
        </div>
    )
  }
}
