import React, { Component } from 'react';
import { fetchProfile } from '../../services/services';
import HeaderComponent from '../Headers/HeaderComponent';
import { fetchCurrencyList, convertCurrency } from '../../services/services';

export default class CurrencyComponent extends Component {
	constructor(props) {
		super(props);
	}

	state = {
		user: {
			email: '',
			first: '',
			last: '',
			type: '',
			ticketsBooked: []
		},
		login: false,
		currenyList: [],
		from: '',
		to: '',
		amount: 'none',
		convertedCurrency: ''
	};

	componentDidMount() {
		fetchProfile().then((user) => {
			if (user) {
				this.setState({
					user: user,
					login: true
				});
			}
		});

		fetchCurrencyList().then((list) => {
			this.setState({ currenyList: list });
		});
	}

	convertButton = () => {

        thithis.state.from === this.state.to ? alert("You have selected the same currency to convert."):

		convertCurrency(this.state.from, this.state.to, Number(this.state.amount)).then((respone) =>{

			this.setState({ convertedCurrency: respone })}
		);
	};

	render() {

		return (
			<div>
				<HeaderComponent user={this.state.user} login={this.state.login} history={this.props.history} />

				<input type="number" onChange={(e) => this.setState({ amount: e.target.value })} />

				<select onChange={(e) => this.setState({ from: e.target.value })}>
					{Object.entries(this.state.currenyList).map(([ key, value ]) => (
						<option value={key} >
							{key}
						</option>
					))}
				</select>

				<select onChange={(e) => this.setState({ to: e.target.value })}>
					{Object.entries(this.state.currenyList).map(([ key, value ]) => (
						<option value={key}>
							{key}
						</option>
					))}
				</select>

				<input readOnly type="text" value={this.state.convertedCurrency} />

				<button className="btn btn-success" onClick={this.convertButton}>
					Convert
				</button>

                    <h1 className="text-white"> {this.state.convertedCurrency} </h1>
			</div>
		);
	}
}
