import React from "react";
import logo from "../../images/logo.png"
import './header-styles.css'
import {RegistrationModalComponent} from "../Register/RegistrationModalComponent";
import LoginModalComponent from "../Login/LoginModalComponent";
import {logoutUser} from "../../services/services";

export default class NavBarComponent extends React.Component {

  logout = () => {
    logoutUser().then(response => {
      this.props.history.push('/')
    })
  };

  render() {

    let pathVariable = '';
    if (this.props.login) {
      pathVariable = 'user-dashboard'
    }
    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand"
             href="#"
             onClick={() => this.props.history.push(`/${pathVariable}`)}
          >
            <img src={logo}
                 style={{"height": "40px"}}
                 alt="logo"/>
          </a>
          <div className="spacer"/>
          {!this.props.login &&
          <>
            <div className="mr-2">
              <button className="btn btn-secondary"
                      data-toggle="modal"
                      data-target="#registrationModal">Register
              </button>
            </div>
            <RegistrationModalComponent
                history={this.props.history}
            />
            <div>
              <button className="btn btn-light"
                      data-toggle="modal"
                      data-target="#loginModal">Login
              </button>
            </div>
            <LoginModalComponent
                history={this.props.history}
            />
          </>}
          {this.props.login &&
          <div className='color-white'>
            <div className="btn-group" style={{"width": "max-content"}}>
              <h4>Hi {this.props.user.first}!</h4>
              <button type="button"
                      className="btn btn-transparent dropdown-toggle color-white"
                      data-toggle="dropdown"
                      aria-haspopup="true"
                      aria-expanded="false">
                More
              </button>
              <div className="dropdown-menu">
                {this.props.user.type === "ADMIN" &&
                <a className="dropdown-item" href="#"
                   onClick={() => this.props.history.push(
                       '/admin')}
                >Admin</a>
                }
                <a className="dropdown-item" href="#"
                   onClick={() => this.props.history.push(
                       '/currency')}
                >Currency</a>
                <a className="dropdown-item" href="#"
                   onClick={() => this.props.history.push(
                       '/search')}
                >Flights</a>
                <a className="dropdown-item" href="#"
                   onClick={() => this.props.history.push(
                       '/')}
                >Home</a>
                <a className="dropdown-item" href="#"
                   onClick={() => this.props.history.push(
                       '/search/hotels')}
                >Hotels</a>
                <a className="dropdown-item" href="#"
                   onClick={() => this.props.history.push(
                       '/profile')}>Profile</a>
                <a className="dropdown-item" href="#"
                   onClick={() => this.props.history.push(
                       '/privacy')}
                >Privacy</a>
                <a className="dropdown-item" href="#"
                   onClick={this.logout}>Logout</a>
              </div>
            </div>
          </div>
          }
        </nav>
    )
  }
}
